import React, {useState, useEffect, useContext, useRef } from 'react';
import { IonModal, IonGrid, IonRow, IonCol, IonButton, IonSlides, IonSlide, IonContent, IonHeader, IonInput } from '@ionic/react';


export function Pokemon_Details_Modal(props) {

    let [details, setDetails] = useState("fetching Overall Details");


    useEffect(() => {


            fetch(props.pokemonDetails)
            .then(res => res.text())
            .then(res => {

                setDetails(res);

            })
    }, []);

    return (
        <React.Fragment>
        {props.openModal == true ?
        (
        <IonModal isOpen={ props.openModal }>
        <IonHeader>
            <IonGrid>
                        <IonRow>
                            <IonCol> Room ID</IonCol>
                            <IonCol> Price Per Night</IonCol>
                        </IonRow>
                        <IonRow>
                            <IonCol> {props.data.roomID} </IonCol>
                            <IonCol> {parseFloat(props.data.roomSalePrice).toFixed(2)} </IonCol>
                        </IonRow>
            </IonGrid>
        </IonHeader>

        <IonContent className='details_gridContent'>
            {viewInventoryBookedRoomDetailData.map((detailData) =>   {
            return (
            <IonGrid class='gridText'>
                <IonRow>
                    <IonCol>
                        <p>
                            {details}
                        </p>
                    </IonCol>

                </IonRow>
            </IonGrid>
                    )
                }
            )}

            </IonContent>

        <IonButton className='button_closeModal_center' onClick={props.closeModal()}> Close </IonButton>

        </IonModal>) : null}
    </React.Fragment>
    )
}
