import React, { useEffect, useState, useRef } from 'react';
import { IonItem, IonButton, IonSlides, IonGrid, IonTabs, IonTab, IonTabBar, IonTabButton, IonToolbar, IonTitle, IonContent, IonHeader, IonLabel, IonRouterOutlet, IonFooter, IonCard, IonText, IonIcon } from '@ionic/react';
import { IonSlide, IonRow, IonCol, IonImg } from '@ionic/react';

import '../css/pokemonApp.css';


export function Pokemon_Overview (props) {


        let [viewAllPokemon, setAllPokemon] = useState([]);




        useEffect(() => {

                fetch('https://pokeapi.co/api/v2/pokemon?limit=898&offset=0')
                .then(res => res.json())
                .then(res => {

                    //alert(res);
                    //alert(Object.entries(res));
                    //alert(Object.entries(res.results).length);
                    //alert(Object.entries(res.results[0]));
                    //alert(Object.entries(res.results[0])[0]);
                    //alert(Object.entries(res.results[0])[1]);
                    //alert(Object.entries(res.results[0])[0][0]);
                    //alert(Object.entries(res.results[0])[0][1]);

                    //let rows = res.toString().split('~~~');

                    for (let rownum = 0; rownum < 20; ++rownum)
                    {
                        //let cols = rows[rownum].split('|');

                        //alert(Object.entries(res.results[rownum])[0][1]);
                        //alert(Object.entries(res.results[rownum])[1][1]);

                        fetch(Object.entries(res.results[rownum])[1][1])
                        .then(res2 => res2.json())
                        .then(res2 => {

                                //alert(Object.entries(Object.entries(res2.abilities[0])[0]));
                                //alert(Object.keys(res2.abilities));

                                //alert("Testing");

                                setAllPokemon((prevAllPokemon) => {
                                    return prevAllPokemon.concat({
                                        id: rownum,
                                        name: Object.entries(res.results[rownum])[0][1],
                                        url: Object.entries(res.results[rownum])[1][1],
                                        image: res2.sprites.other['official-artwork'].front_default,
                                        height: res2.height,
                                        weight: res2.weight,
                                        //abilities: Object.entries(res2.abilities[0]),

                                    })
                                });
                        })

                        /*setAllPokemon((prevAllPokemon) => {
                            return prevAllPokemon.concat({
                                id: rownum,
                                name: Object.entries(res.results[rownum])[0][1],
                                url: Object.entries(res.results[rownum])[1][1],
                            })
                        });*/
                    } 
                })
        }, []);


        return (
            <React.Fragment>

            <IonSlides pager='true' options={{direction: 'vertical'}}>
                {viewAllPokemon.map((detailData) =>   {
                        return (
                                <IonSlide key={detailData.id}>
                                    <IonCard className='pokemon_showcase_card' style={{width: '50%', height: '50%'}}>
                                        <IonImg className='pokemon_showcase_main' src={detailData.image} style={{width: '50%', height: '50%'}}></IonImg>
                                        <IonGrid>
                                            <IonRow>
                                                <IonCol size='12'>
                                                    <IonText>{detailData.name}</IonText>
                                                </IonCol>
                                            </IonRow>
                                            <IonRow>
                                                <IonCol size='12'>
                                                    <IonText>Height: {detailData.height} / Weight: {detailData.weight}</IonText>
                                                </IonCol>
                                            </IonRow>
                                            {/*<IonRow>
                                                <IonCol size='12'>
                                                    <IonText>Abilities: {detailData.abilities}</IonText>
                                                </IonCol>
                                            </IonRow>*/}
                                            <IonRow>
                                                <IonCol size='12'>
                                                    <IonButton>Details</IonButton>
                                                </IonCol>
                                            </IonRow>
                                        </IonGrid>

                                    </IonCard>
                                </IonSlide>
                        );
                    })}

            </IonSlides>

            </React.Fragment>

        )

    }
