import React, { useEffect, useState, useRef } from 'react';
import { IonItem, IonButton, IonSlides, IonGrid, IonTabs, IonTab, IonTabBar, IonTabButton, IonToolbar, IonTitle, IonContent, IonHeader, IonLabel, IonRouterOutlet, IonFooter, IonCard } from '@ionic/react';
import { IonSlide, IonRow, IonCol, IonImg } from '@ionic/react';

import '../css/pokemonApp.css';


export function Pokemon_Overview (props) {


        let [viewAllPokemon, setAllPokemon] = useState([]);




        useEffect(() => {

                fetch('https://pokeapi.co/api/v2/pokemon?limit=898&offset=0')
                .then(res => res.json())
                .then(res => {

                    //alert(res);
                    //alert(Object.entries(res));
                    //alert(Object.entries(res.results).length);
                    //alert(Object.entries(res.results[0]));
                    //alert(Object.entries(res.results[0])[0]);
                    //alert(Object.entries(res.results[0])[1]);
                    //alert(Object.entries(res.results[0])[0][0]);
                    //alert(Object.entries(res.results[0])[0][1]);

                    //let rows = res.toString().split('~~~');

                    for (let rownum = 0; rownum < 20; ++rownum)
                    {
                        //let cols = rows[rownum].split('|');

                        //alert(Object.entries(res.results[rownum])[0][1]);
                        //alert(Object.entries(res.results[rownum])[1][1]);

                        fetch(Object.entries(res.results[rownum])[1][1])
                        .then(res2 => res2.json())
                        .then(res2 => {

                            for (let rownum2 = 0; rownum2 < 20; ++rownum)
                            {

                                alert(Object.entries(res.results[rownum])[0][1]);
                                alert(Object.entries(res.results[rownum])[1][1]);

                                alert(Object.entries(res2));
                                alert(Object.entries(res2.height));
                                alert(Object.entries(res2.weight));
                                alert(Object.entries(res2.sprites));
                                alert(Object.entries(res2.sprites)[0]);
                                alert(Object.entries(res2.sprites)[4]);
                                alert(Object.entries(res2.sprites)[7]);

                                alert("Testing");

                                alert(Object.entries(res2.official[0])[0][1]);
                                alert(Object.entries(res2.height[0])[0]);
                                alert(Object.entries(res2.weight[0])[0]);
                                alert(Object.entries(res2.abilities[0]));

                                setAllPokemon((prevAllPokemon) => {
                                    return prevAllPokemon.concat({
                                        id: rownum,
                                        name: Object.entries(res.results[rownum])[0][1],
                                        url: Object.entries(res.results[rownum])[1][1],
                                        image: Object.entries(res2.official[0])[0][1],
                                        height: Object.entries(res2.height[0])[0],
                                        weight: Object.entries(res2.weight[0])[0],
                                        abilities: Object.entries(res2.abilities[0]),

                                    })
                                });

                            }
                        })

                        /*setAllPokemon((prevAllPokemon) => {
                            return prevAllPokemon.concat({
                                id: rownum,
                                name: Object.entries(res.results[rownum])[0][1],
                                url: Object.entries(res.results[rownum])[1][1],
                            })
                        });*/
                    } 
                })
        }, []);


        return (
            <React.Fragment>

            <IonSlides pager='true' options={{direction: 'vertical'}}>
                {viewAllPokemon.map((detailData) =>   {
                        return (
                                <IonSlide key={detailData.id}>
                                        <IonImg className='pokemon_showcase_main' src={detailData.image} style={{width: '75%', height: '75%'}}></IonImg>
                                </IonSlide>
                        );
                    })}
            </IonSlides>

            </React.Fragment>

        )

    }
